import { Component } from '@angular/core';

@Component({
  selector: 'app-pokemon-card',
  templateUrl: './pokemon-card.component.html',
  styleUrls: ['./pokemon-card.component.sass'],
})
export class PokemonCardComponent {}
<div class="pokemon-card">
  <div class="pokemon-card__img">
    <img src="https://assets.pokemon.com/assets/cms2/img/pokedex/detail/001.png" alt="" srcset="" />
  </div>
  <div class="pokemon-card__number">Nº 0001</div>
  <div class="pokemon-card__name">Bulbasaur</div>
  <div class="pokemon-card__types pokemon-card__types--grass">Grass</div>
  <div class="pokemon-card__types pokemon-card__types--poison">Poison</div>
</div>
